mutation PermissionGroupUpdate(
    $id: ID!, $input: PermissionGroupUpdateInput!) {
    permissionGroupUpdate(
        id: $id, input: $input)
    {
        group{
            id
            name
            permissions {
                name
                code
            }
        }
        permissionGroupErrors{
            field
            code
            permissions
            users
            message
        }
    }
}
