query (
    $first: Int, $last: Int, $after: String, $before: String,
    $sortBy: ProductOrder, $filter: ProductFilterInput
){
    products (
        first: $first, last: $last, after: $after, before: $before,
        sortBy: $sortBy, filter: $filter
    ) {
        edges {
            node {
                name
                slug
            }
        }
        pageInfo{
            startCursor
            endCursor
            hasNextPage
            hasPreviousPage
        }
    }
}
