mutation unAssignAttribute(
  $productTypeId: ID!, $attributeIds: [ID]!
) {
  attributeUnassign(productTypeId: $productTypeId, attributeIds: $attributeIds) {
    errors {
      field
      message
    }
    productType {
      id
      variantAttributes {
        id
      }
      productAttributes {
        id
      }
    }
  }
}
