query:
mutation CreateStaff(
        $email: String, $redirect_url: String, $add_groups: [ID!]
    ) {
    staffCreate(input: {email: $email, redirectUrl: $redirect_url,
        addGroups: $add_groups}
    ) {
        staffErrors {
            field
            code
            permissions
            groups
        }
        user {
            id
            email
            isStaff
            isActive
            userPermissions {
                code
            }
            permissions {
                code
            }
            permissionGroups {
                name
                permissions {
                    code
                }
            }
            avatar {
                url
            }
        }
    }
}
