query:
fragment Money on Money {
  amount
  currency
  __typename
}

fragment ProductFragment on Product {
  id
  name
  thumbnail {
    url
    __typename
  }
  isAvailable
  isPublished
  productType {
    id
    name
    hasVariants
    __typename
  }
  __typename
}

query ProductList($first: Int, $after: String, $last: Int, $before: String, $filter: ProductFilterInput, $sort: ProductOrder) {
  products(before: $before, after: $after, first: $first, last: $last, filter: $filter, sortBy: $sort) {
    edges {
      node {
        ...ProductFragment
        attributes {
          attribute {
            id
            __typename
          }
          values {
            id
            name
            __typename
          }
          __typename
        }
        pricing {
          priceRangeUndiscounted {
            start {
              gross {
                ...Money
                __typename
              }
              __typename
            }
            stop {
              gross {
                ...Money
                __typename
              }
              __typename
            }
            __typename
          }
          __typename
        }
        __typename
      }
      __typename
    }
    pageInfo {
      hasPreviousPage
      hasNextPage
      startCursor
      endCursor
      __typename
    }
    totalCount
    __typename
  }
}
