query:
query products(
  $field: ProductOrderField
  $attributeId: ID
  $direction: OrderDirection!
) {
  products(
    first: 100
    sortBy: { field: $field, attributeId: $attributeId, direction: $direction }
  ) {
    edges {
      node {
        name
        attributes {
          attribute {
            slug
          }
          values {
            name
          }
        }
      }
    }
  }
}
