query:
fragment Money on Money {
  amount
  currency
  __typename
}

fragment CategoryFragment on Category {
  id
  name
  children {
    totalCount
    __typename
  }
  products {
    totalCount
    __typename
  }
  __typename
}

fragment MetadataItem on MetadataItem {
  key
  value
  __typename
}

fragment MetadataFragment on ObjectWithMetadata {
  metadata {
    ...MetadataItem
    __typename
  }
  privateMetadata {
    ...MetadataItem
    __typename
  }
  __typename
}

fragment CategoryDetailsFragment on Category {
  id
  ...MetadataFragment
  backgroundImage {
    alt
    url
    __typename
  }
  name
  slug
  descriptionJson
  seoDescription
  seoTitle
  parent {
    id
    __typename
  }
  __typename
}

fragment PageInfoFragment on PageInfo {
  endCursor
  hasNextPage
  hasPreviousPage
  startCursor
  __typename
}

query CategoryDetails($id: ID!, $first: Int, $after: String, $last: Int, $before: String) {
  category(id: $id) {
    ...CategoryDetailsFragment
    children(first: $first, after: $after, last: $last, before: $before) {
      edges {
        node {
          ...CategoryFragment
          __typename
        }
        __typename
      }
      pageInfo {
        ...PageInfoFragment
        __typename
      }
      __typename
    }
    products(first: $first, after: $after, last: $last, before: $before) {
      pageInfo {
        ...PageInfoFragment
        __typename
      }
      edges {
        cursor
        node {
          id
          name
          isAvailable
          thumbnail {
            url
            __typename
          }
          productType {
            id
            name
            __typename
          }
          pricing {
            priceRangeUndiscounted {
              start {
                gross {
                  ...Money
                  __typename
                }
                __typename
              }
              stop {
                gross {
                  ...Money
                  __typename
                }
                __typename
              }
              __typename
            }
            __typename
          }
          __typename
        }
        __typename
      }
      __typename
    }
    __typename
  }
}
