query:
query OrdersQuery {
    orders(first: 1) {
        edges {
            node {
                events {
                    date
                    type
                    user {
                        email
                    }
                    message
                    email
                    emailType
                    amount
                    quantity
                    composedId
                    orderNumber
                    fulfilledItems {
                        quantity
                        orderLine {
                            productName
                            variantName
                        }
                    }
                    paymentId
                    paymentGateway
                    warehouse {
                        name
                    }
                }
            }
        }
    }
}
