query:
mutation updateProduct(
    $productId: ID!,
    $categoryId: ID!,
    $name: String!,
    $slug: String!,
    $descriptionJson: JSONString!,
    $isPublished: Boolean!,
    $visibleInListings: Boolean!,
    $chargeTaxes: Boolean!,
    $taxCode: String!,
    $basePrice: PositiveDecimal!,
    $attributes: [AttributeValueInput!]) {
        productUpdate(
            id: $productId,
            input: {
                category: $categoryId,
                name: $name,
                slug: $slug,
                descriptionJson: $descriptionJson,
                isPublished: $isPublished,
                visibleInListings: $visibleInListings,
                chargeTaxes: $chargeTaxes,
                taxCode: $taxCode,
                basePrice: $basePrice,
                attributes: $attributes
            }) {
                product {
                    category {
                        name
                    }
                    descriptionJson
                    isPublished
                    publicationDate
                    chargeTaxes
                    variants {
                        price {
                            amount
                        }
                    }
                    taxType {
                        taxCode
                        description
                    }
                    name
                    slug
                    productType {
                        name
                    }
                    attributes {
                        attribute {
                            id
                            name
                        }
                        values {
                            name
                            slug
                        }
                    }
                    visibleInListings
                    }
                    errors {
                    message
                    field
                    }
                }
                }
