query (
    $first: Int, $last: Int, $after: String, $before: String,
    $sortBy: UserSortingInput, $filter: CustomerFilterInput
){
    customers(
        first: $first, last: $last, after: $after, before: $before,
        sortBy: $sortBy, filter: $filter
    ) {
        edges {
            node {
                firstName
            }
        }
        pageInfo{
            startCursor
            endCursor
            hasNextPage
            hasPreviousPage
        }
    }
}
