query:
mutation createProduct(
    $input: ProductCreateInput!
) {
         productCreate(
             input: $input) {
                 product {
                     category {
                         name
                     }
                     descriptionJson
                     isPublished
                     publicationDate
                     chargeTaxes
                     taxType {
                         taxCode
                         description
                     }
                     name
                     slug
                     productType {
                         name
                     }
                     attributes {
                         attribute {
                             slug
                         }
                         values {
                             slug
                         }
                     }
                     visibleInListings
                   }
                   productErrors {
                     field
                     code
                     message
                     attributes
                   }
                 }
               }
