query:
mutation giftCardCreate(
    $code: String, $startDate: Date, $endDate: Date,
    $balance: PositiveDecimal!, $userEmail: String) {
        giftCardCreate(input: {
                code: $code, startDate: $startDate,
                endDate: $endDate,
                balance: $balance, userEmail: $userEmail }) {
            errors {
                field
                message
            }
            giftCardErrors {
                field
                message
                code
            }
            giftCard {
                displayCode
                user {
                    email
                }
                created
                startDate
                endDate
                lastUsedOn
                isActive
                initialBalance {
                    amount
                }
                currentBalance {
                    amount
                }
            }
        }
    }
