mutation updateShipping(
    $id: ID!
    $name: String
    $default: Boolean
    $countries: [String]
    $addWarehouses: [ID]
    $removeWarehouses: [ID]
) {
    shippingZoneUpdate(
        id: $id
        input: {
            name: $name
            default: $default
            countries: $countries
            addWarehouses: $addWarehouses
            removeWarehouses: $removeWarehouses
        }
    ) {
        shippingZone {
            name
            warehouses {
                name
                slug
            }
        }
        shippingErrors {
            field
            code
            warehouses
        }
    }
}
