query:
    mutation updateAttribute(
    $id: ID!, $slug: String, $name: String) {
    attributeUpdate(
            id: $id,
            input: {
                slug: $slug, name: $name}) {
        errors {
            field
            message
        }
        productErrors {
            field
            message
            code
        }
        attribute {
            name
            slug
        }
    }
}
