mutation ProductUpdate(
    $productId: ID!,
    $basePrice: PositiveDecimal!,
) {
    productUpdate(
        id: $productId
        input: {
            basePrice: $basePrice
        }
    ) {
        product {
            name
            minimalVariantPrice {
                amount
            }
        }
        errors {
            message
            field
        }
    }
}
