query:
mutation ProductTypeReorderAttributes(
  $productTypeId: ID!
  $moves: [ReorderInput]!
  $type: AttributeTypeEnum!
) {
  productTypeReorderAttributes(
    productTypeId: $productTypeId
    moves: $moves
    type: $type
  ) {
    productType {
      id
      variantAttributes {
        id
        slug
      }
      productAttributes {
        id
      }
    }

    errors {
      field
      message
    }
  }
}
