query:
mutation giftCardUpdate(
    $id: ID!, $startDate: Date, $endDate: Date,
    $balance: PositiveDecimal, $userEmail: String!) {
        giftCardUpdate(id: $id, input: {startDate: $startDate,
                endDate: $endDate,
                balance: $balance, userEmail: $userEmail}) {
            errors {
                field
                message
            }
            giftCard {
                id
                displayCode
                currentBalance {
                    amount
                }
                user {
                    email
                }
            }
        }
    }
