mutation digitalUpdate($variant: ID!, $input: DigitalContentInput!){
    digitalContentUpdate(variantId:$variant, input: $input){
        variant{
            id
        }
        content{
            contentFile
            maxDownloads
            urlValidDays
            automaticFulfillment
        }
        errors {
            field
            message
        }
        productErrors {
            field
            message
            code
        }
    }
}
