query:
    mutation PermissionGroupCreate(
    $input: PermissionGroupCreateInput!) {
    permissionGroupCreate(
        input: $input)
    {
        group{
            id
            name
            permissions {
                name
                code
            }
            users {
                email
            }
        }
        permissionGroupErrors{
            field
            code
            permissions
            users
            message
        }
    }
}
