mutation checkoutLinesAdd(
        $checkoutId: ID!, $lines: [CheckoutLineInput!]!) {
    checkoutLinesAdd(checkoutId: $checkoutId, lines: $lines) {
        checkout {
            token
            lines {
                quantity
                variant {
                    id
                }
            }
        }
        checkoutErrors {
            field
            code
            message
            variants
        }
    }
}
