mutation ProductVariantCreate(
    $productId: ID!,
    $sku: String!,
    $price: PositiveDecimal,
    $attributes: [AttributeValueInput]!,
) {
    productVariantCreate(
        input: {
            product: $productId,
            sku: $sku,
            price: $price,
            attributes: $attributes
        }
    ) {
        productVariant {
            name
        }
        productErrors {
            message
            field
        }
    }
}
