mutation StaffNotificationRecipient ($input: StaffNotificationRecipientInput!) {
    staffNotificationRecipientCreate(input: $input) {
        staffNotificationRecipient {
            active
            email
            user {
                id
                firstName
                lastName
                email
            }
        }
        shopErrors {
            field
            message
            code
        }
    }
}
