mutation updateWarehouse($input: WarehouseUpdateInput!, $id: ID!) {
    updateWarehouse(id: $id, input: $input) {
        warehouseErrors {
            message
            field
            code
        }
        warehouse {
            name
            slug
            companyName
            address {
                id
                streetAddress1
                streetAddress2
            }
        }
    }
}
