query {
  products(first: 1) {
    edges {
      node {
        variants {
          isAvailable
          pricing {
            onSale

            discount {
              currency
              net {
                amount
              }
            }

            priceUndiscounted {
              currency
              net {
                amount
              }
            }

            price {
              currency
              net {
                amount
              }
            }
          }
        }
      }
    }
  }
}
