query (
    $first: Int, $last: Int, $after: String, $before: String,
    $sortBy: AppSortingInput, $filter: AppFilterInput
){
    apps(
        first: $first, last: $last, after: $after, before: $before,
        sortBy: $sortBy, filter: $filter
    ) {
        edges{
            node{
                name
            }
        }
        pageInfo{
            startCursor
            endCursor
            hasNextPage
            hasPreviousPage
        }
    }
}
