mutation($checkoutId: ID!, $promoCode: String!) {
    checkoutAddPromoCode(
        checkoutId: $checkoutId, promoCode: $promoCode) {
        errors {
            field
            message
        }
        checkout {
            id,
            voucherCode
            giftCards {
                id
                displayCode
            }
            totalPrice {
                gross {
                    amount
                }
            }
        }
    }
}
