mutation attributeValueTranslate($attributeValueId: ID!) {
    attributeValueTranslate(
            id: $attributeValueId, languageCode: PL,
            input: {name: "Róż PL"}) {
        attributeValue {
            translation(languageCode: PL) {
                name
                language {
                    code
                }
            }
        }
    }
}
