mutation menuItemTranslate($menuItemId: ID!) {
    menuItemTranslate(
            id: $menuItemId, languageCode: PL,
            input: {name: "Odnośnik PL"}) {
        menuItem {
            translation(languageCode: PL) {
                name
                language {
                    code
                }
            }
        }
    }
}
