query:
fragment PageInfoFragment on PageInfo {
  endCursor
  hasNextPage
  hasPreviousPage
  startCursor
  __typename
}

fragment AttributeTranslationFragment on Attribute {
  id
  name
  translation(languageCode: $language) {
    id
    name
    __typename
  }
  values {
    id
    name
    translation(languageCode: $language) {
      id
      name
      __typename
    }
    __typename
  }
  __typename
}

fragment ProductTypeTranslationFragment on ProductType {
  id
  name
  productAttributes {
    ...AttributeTranslationFragment
    __typename
  }
  variantAttributes {
    ...AttributeTranslationFragment
    __typename
  }
  __typename
}

query ProductTypeTranslations($language: LanguageCodeEnum!, $first: Int, $after: String, $last: Int, $before: String, $filter: ProductTypeFilterInput) {
  productTypes(before: $before, after: $after, first: $first, last: $last, filter: $filter) {
    edges {
      node {
        ...ProductTypeTranslationFragment
        __typename
      }
      __typename
    }
    pageInfo {
      ...PageInfoFragment
      __typename
    }
    __typename
  }
}
