query OrdersQuery {
    orders(first: 1) {
        edges {
            node {
                number
                canFinalize
                status
                statusDisplay
                paymentStatus
                paymentStatusDisplay
                userEmail
                isPaid
                shippingPrice {
                    gross {
                        amount
                    }
                }
                lines {
                    id
                }
                fulfillments {
                    fulfillmentOrder
                }
                payments{
                    id
                }
                subtotal {
                    net {
                        amount
                    }
                }
                total {
                    net {
                        amount
                    }
                }
                availableShippingMethods {
                    id
                    price {
                        amount
                    }
                    minimumOrderPrice {
                        amount
                        currency
                    }
                    type
                }
            }
        }
    }
}
