mutation ProductVariantStocksCreate($variantId: ID!, $stocks: [StockInput!]!){
    productVariantStocksCreate(variantId: $variantId, stocks: $stocks){
        productVariant{
            stocks {
                quantity
                quantityAllocated
                id
                warehouse{
                    slug
                }
            }
        }
        bulkStockErrors{
            code
            field
            message
            index
        }
    }
}
