query:
mutation saleTranslate($saleId: ID!) {
    saleTranslate(
            id: $saleId, languageCode: PL,
            input: {name: "Wyprz PL"}) {
        sale {
            translation(languageCode: PL) {
                name
                language {
                    code
                }
            }
        }
    }
}
