query:
mutation accountUpdate(
        $billing: AddressInput, $shipping: AddressInput, $firstName: String,
        $lastName: String) {
    accountUpdate(
      input: {
        defaultBillingAddress: $billing,
        defaultShippingAddress: $shipping,
        firstName: $firstName,
        lastName: $lastName,
    }) {
        errors {
            field
            message
        }
        user {
            firstName
            lastName
            email
            defaultBillingAddress {
                id
            }
            defaultShippingAddress {
                id
            }
        }
    }
}
