query OrdersQuery {
    orders(first: 1) {
        edges {
            node {
                events {
                    type
                    user {
                        email
                    }
                    message
                    email
                    emailType
                    amount
                    quantity
                    composedId
                    orderNumber
                    lines {
                        quantity
                    }
                    paymentId
                    paymentGateway
                }
            }
        }
    }
}
