mutation updateProduct(
    $productId: ID!,
    $sku: String,
    $trackInventory: Boolean)
{
    productUpdate(
        id: $productId,
        input: {
            sku: $sku,
            trackInventory: $trackInventory,
        })
    {
        product {
            id
            variants{
                id
                sku
                trackInventory
            }
        }
        errors {
            message
            field
        }
    }
}
