query:
fragment BasicProductFields on Product {
  id
  name
  thumbnail {
    url
    alt
  }
  thumbnail2x: thumbnail(size: 510) {
    url
  }
}

fragment ProductVariantFields on ProductVariant {
  id
  sku
  name
  stockQuantity
  isAvailable
  pricing {
    discountLocalCurrency {
      currency
      gross {
        amount
        localized
      }
    }
    price {
      currency
      gross {
        amount
        localized
      }
    }
    priceUndiscounted {
      currency
      gross {
        amount
        localized
      }
    }
    priceLocalCurrency {
      currency
      gross {
        amount
        localized
      }
    }
  }
  attributes {
    attribute {
      id
      name
    }
    values {
      id
      name
      value: name
    }
  }
}

query ProductDetails($id: ID!) {
  product(id: $id) {
    ...BasicProductFields
    description
    category {
      id
      name
      products(first: 4) {
        edges {
          node {
            ...BasicProductFields
            category {
              id
              name
            }
            pricing {
              priceRange {
                start{
                  currency
                  gross {
                    amount
                    localized
                  }
                }
                stop{
                  currency
                  gross {
                    amount
                    localized
                  }
                }
              }
              priceRangeUndiscounted {
                start{
                  currency
                  gross {
                    amount
                    localized
                  }
                }
                stop{
                  currency
                  gross {
                    amount
                    localized
                  }
                }
              }
              priceRangeLocalCurrency {
                start{
                  currency
                  gross {
                    amount
                    localized
                  }
                }
                stop{
                  currency
                  gross {
                    amount
                    localized
                  }
                }
              }
            }
          }
        }
      }
    }
    images {
      id
      url
    }
    variants {
      ...ProductVariantFields
    }
    seoDescription
    seoTitle
    isAvailable
  }
}
