query:
mutation createVariant (
      $productId: ID!,
      $sku: String,
      $stocks: [StockInput!],
      $price: PositiveDecimal,
      $costPrice: PositiveDecimal,
      $attributes: [AttributeValueInput]!,
      $weight: WeightScalar,
      $trackInventory: Boolean) {
          productVariantCreate(
              input: {
                  product: $productId,
                  sku: $sku,
                  stocks: $stocks,
                  price: $price,
                  costPrice: $costPrice,
                  attributes: $attributes,
                  trackInventory: $trackInventory,
                  weight: $weight
              }) {
              productErrors {
                field
                message
                attributes
                code
              }
              productVariant {
                  name
                  sku
                  attributes {
                      attribute {
                          slug
                      }
                      values {
                          slug
                      }
                  }
                  price {
                      currency
                      amount
                      localized
                  }
                  costPrice {
                      currency
                      amount
                      localized
                  }
                  weight {
                      value
                      unit
                  }
                  stocks {
                      quantity
                      warehouse {
                          slug
                      }
                  }
              }
          }
      }
