query:
query {
    category(id: "Q2F0ZWdvcnk6Mzcz") {
        products(first: 20) {
            edges {
                node {
                    id
                    name
                    url
                    slug
                    thumbnail{
                        url
                        alt
                    }
                    images {
                        url
                    }
                    variants {
                        name
                    }
                    isAvailable
                    pricing {
                        priceRange {
                            start {
                                gross {
                                    amount
                                    currency
                                    localized
                                }
                                net {
                                    amount
                                    currency
                                    localized
                                }
                                currency
                            }
                        }
                    }
                    purchaseCost {
                        start {
                            amount
                        }
                        stop {
                            amount
                        }
                    }
                    margin {
                        start
                        stop
                    }
                }
            }
        }
    }
}
