query:
mutation VariantUpdate(
  $id: ID!
  $attributes: [AttributeValueInput]
  $costPrice: PositiveDecimal
  $price: PositiveDecimal
  $sku: String
  $trackInventory: Boolean!
) {
  productVariantUpdate(
    id: $id
    input: {
      attributes: $attributes
      costPrice: $costPrice
      price: $price
      sku: $sku
      trackInventory: $trackInventory
    }
  ) {
    errors {
      field
      message
    }
    productVariant {
      id
      attributes {
        attribute {
          id
          name
          slug
          values {
            id
            name
            slug
            __typename
          }
          __typename
        }
        __typename
      }
    }
  }
}
