query translation(
    $kind: TranslatableKinds!, $id: ID!, $languageCode: LanguageCodeEnum!
){
    translation(kind: $kind, id: $id){
        __typename
        ...on SaleTranslatableContent{
            id
            name
            translation(languageCode: $languageCode){
                name
            }
            sale {
                id
                name
            }
        }
    }
}
