query:
fragment Address on Address {
  id
  firstName
  lastName
  companyName
  streetAddress1
  streetAddress2
  city
  postalCode
  country {
    code
    country
  }
  countryArea
  phone
  isDefaultBillingAddress
  isDefaultShippingAddress
}

fragment Price on TaxedMoney {
  gross {
    amount
    currency
  }
  net {
    amount
    currency
  }
}

    fragment ProductVariant on ProductVariant {
      id
      name
      pricing {
        onSale
        priceUndiscounted {
          ...Price
        }
        price {
          ...Price
        }
      }
      product {
        id
        name
        thumbnail {
          url
          alt
        }
        thumbnail2x: thumbnail(size: 510) {
          url
        }
      }
    }

    fragment OrderDetail on Order {
      userEmail
      paymentStatus
      paymentStatusDisplay
      status
      statusDisplay
      id
      number
      shippingAddress {
        ...Address
      }
      lines {
        productName
        quantity
        variant {
          ...ProductVariant
        }
        unitPrice {
          currency
          ...Price
        }
      }
      subtotal {
        ...Price
      }
      total {
        ...Price
      }
      shippingPrice {
        ...Price
      }
    }

        query OrderByToken($token: UUID!) {
          orderByToken(token: $token) {
            ...OrderDetail
          }
        }
