query:
fragment Price on TaxedMoney {
  gross {
    amount
    currency
    __typename
  }
  net {
    amount
    currency
    __typename
  }
  __typename
}

fragment ProductVariant on ProductVariant {
  id
  name
  sku
  quantityAvailable
  isAvailable
  pricing {
    onSale
    priceUndiscounted {
      ...Price
      __typename
    }
    price {
      ...Price
      __typename
    }
    __typename
  }
  attributes {
    attribute {
      id
      name
      __typename
    }
    values {
      id
      name
      value: name
      __typename
    }
    __typename
  }
  product {
    id
    name
    thumbnail {
      url
      alt
      __typename
    }
    thumbnail2x: thumbnail(size: 510) {
      url
      __typename
    }
    productType {
      id
      isShippingRequired
      __typename
    }
    __typename
  }
  __typename
}

query CheckoutProductVariants($ids: [ID]) {
  productVariants(ids: $ids, first: 100) {
    edges {
      node {
        ...ProductVariant
        __typename
      }
      __typename
    }
    __typename
  }
}
