query:
fragment BasicProductFields on Product {
  id
  name
  thumbnail {
    url
    alt
  }
  thumbnail2x: thumbnail(size: 510) {
    url
  }
}

fragment ProductVariantFields on ProductVariant {
  id
  sku
  name
  stockQuantity
  isAvailable
  pricing {
    discountLocalCurrency {
      currency
      gross {
        amount
        localized
      }
    }
    price {
      currency
      gross {
        amount
        localized
      }
    }
    priceUndiscounted {
      currency
      gross {
        amount
        localized
      }
    }
    priceLocalCurrency {
      currency
      gross {
        amount
        localized
      }
    }
  }
  attributes {
    attribute {
      id
      name
    }
    values {
      id
      name
      value: name
    }
  }
}

query VariantList($ids: [ID!]) {
  productVariants(ids: $ids, first: 100) {
    edges {
      node {
        ...ProductVariantFields
        stockQuantity
        quantityAvailable
        quantityAvailablePl: quantityAvailable(countryCode: PL)
        quantityAvailableUS: quantityAvailable(countryCode: US)
        product {
          ...BasicProductFields
        }
      }
    }
  }
}
