query:
fragment User on User {
  id
  email
  firstName
  lastName
  userPermissions {
    code
    name
    __typename
  }
  avatar {
    url
    __typename
  }
  __typename
}

mutation TokenAuth($email: String!, $password: String!) {
  tokenCreate(email: $email, password: $password) {
    errors: accountErrors {
      field
      message
      __typename
    }
    csrfToken
    token
    user {
      ...User
      __typename
    }
    __typename
  }
}
