query:
mutation updateVariant(
    $id: ID!
    $sku: String!
    $price: PositiveDecimal
    $costPrice: PositiveDecimal,
    $attributes: [AttributeValueInput],
) {
    productVariantUpdate(
        id: $id
        input: {
            sku: $sku,
            price: $price,
            costPrice: $costPrice,
            attributes: $attributes,
        }
    ) {
        productErrors {
            field
            message
            code
        }
    }
}
