query:
fragment StaffMemberFragment on User {
  id
  email
  firstName
  isActive
  lastName
  __typename
}

query StaffList($first: Int, $after: String, $last: Int, $before: String, $filter: StaffUserInput, $sort: UserSortingInput) {
  staffUsers(before: $before, after: $after, first: $first, last: $last, filter: $filter, sortBy: $sort) {
    edges {
      cursor
      node {
        ...StaffMemberFragment
        avatar(size: 48) {
          url
          __typename
        }
        __typename
      }
      __typename
    }
    pageInfo {
      hasPreviousPage
      hasNextPage
      startCursor
      endCursor
      __typename
    }
    __typename
  }
}
