mutation createShipping(
    $name: String, $default: Boolean, $countries: [String], $addWarehouses: [ID] ){
    shippingZoneCreate(
        input: {
            name: $name, countries: $countries,
            default: $default, addWarehouses: $addWarehouses
        })
    {
        shippingErrors {
            field
            code
        }
        shippingZone {
            name
            countries {
                code
            }
            default
            warehouses {
                name
            }
        }
    }
}
