mutation ProductVariantBulkCreate(
    $variants: [ProductVariantBulkCreateInput]!, $productId: ID!
) {
    productVariantBulkCreate(variants: $variants, product: $productId) {
        bulkProductErrors {
            field
            message
            code
            index
            warehouses
        }
        productVariants{
            id
            sku
            price {
                amount
            }
            stocks {
                warehouse {
                    slug
                }
                quantity
            }
        }
        count
    }
}
