query:
fragment MainMenuSubItem on MenuItem {
  id
  name
  category {
    id
    name
  }
  url
  collection {
    id
    name
  }
  page {
    slug
  }
  parent {
    id
  }
}

query MainMenu {
  shop {
    navigation {
      main {
        id
        items {
          ...MainMenuSubItem
          children {
            ...MainMenuSubItem
            children {
              ...MainMenuSubItem
            }
          }
        }
      }
    }
  }
}
