query:
mutation  saleCreate(
        $type: DiscountValueTypeEnum, $name: String, $value: PositiveDecimal,
        $startDate: DateTime, $endDate: DateTime) {
    saleCreate(input: {
            name: $name, type: $type, value: $value,
            startDate: $startDate, endDate: $endDate}) {
        sale {
            type
            name
            value
            startDate
            endDate
        }
        discountErrors {
            field
            code
            message
        }
    }
}
